import React, { Component } from 'react';
import s from './SignUpForm.module.css';

const INITIAL_STATE = {
  login: '',
  email: '',
  // phone: '',
  password: '',
  // passwordRepeat: '',
};

export default class SignUpForm extends Component {
  state = { ...INITIAL_STATE };

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleSubmit = evt => {
    evt.preventDefault();
    const { login, email, phone, password, passwordRepeat } = this.state;
    console.log(`
      Login: ${login}
      Email: ${email}
      Phone: ${phone}
      Password: ${password}
      PasswordRepeat: ${passwordRepeat}
    `);
    this.reset();
  };

  reset = () => {
    this.setState({ ...INITIAL_STATE });
  };

  render() {
    // const { login, email, phone, password, passwordRepeat } = this.state;
    const { login, email, password } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        <label className={s.label} htmlFor="login">
          Name
          <input
            id="login"
            className={s.input}
            type="text"
            placeholder="Enter login"
            name="login"
            value={login}
            onChange={this.handleChange}
          />
        </label>
        <label className={s.label} htmlFor="email">
          Email
          <input
            id="email"
            className={s.input}
            type="email"
            placeholder="Enter email"
            name="email"
            value={email}
            onChange={this.handleChange}
          />
        </label>
        {/* <label className={s.label} htmlFor="phoneFormIn">
          Phone
          <input
            id="phoneFormIn"
            className={s.input}
            type="phone"
            placeholder="Enter phone"
            name="phone"
            value={phone}
            onChange={this.handleChange}
          />
        </label> */}
        <label className={s.label} htmlFor="password">
          Password
          <input
            id="password"
            className={s.input}
            type="password"
            placeholder="Enter password"
            name="password"
            value={password}
            onChange={this.handleChange}
          />
        </label>
        {/* <label className={s.label} htmlFor="passwordFormInRepeat">
          Password Repeat
          <input
            id="passwordFormInRepeat"
            className={s.input}
            type="password"
            placeholder="Enter password"
            name="passwordRepeat"
            value={passwordRepeat}
            onChange={this.handleChange}
          />
        </label> */}
        <button type="submit">Sign Up</button>
      </form>
    );
  }
}
