import React from 'react';
import { connect } from 'react-redux';
import userAvatar from '../../modules/user/UserMenu/userAvatar.jpg';

const Account = ({name='', email=''}) => state.session.user &&(
  <section className="account">
    <div className="text">
      <div>
        <img src={userAvatar} alt="user avatar" title="User profile" />
        <p>Name: {name}</p>
        <p>Phone: +38-093-111-11-11</p>
        <p>Email: {email}</p>
      </div>
    </div>
  </section>
);

const mapStateToProps = (state) => ({
  name: state.session.user.name,
  email: state.session.user.email,
});

export default connect(
  mapStateToProps
)(Account);
